let currentQuestion = 0;
let score = 0;


const quizImage = document.getElementById("quizImage");
const optionsContainer = document.getElementById("optionsContainer");
const nextBtn = document.getElementById("nextBtn");
const resultDiv = document.getElementById("result");
const restartBtn = document.getElementById("restartBtn");

function loadQuestion() {
    const question = quizData[currentQuestion];
    quizImage.src = question.image;
    optionsContainer.innerHTML = "";
    nextBtn.classList.add("hidden");
    
    
    question.options.forEach(optionText => {
        const optionEl = document.createElement("div");
        optionEl.classList.add("option");
        optionEl.textContent = optionText;
        optionEl.addEventListener("click", () => selectOption(optionEl, question.correct));
        optionsContainer.appendChild(optionEl);
    });
}

function selectOption(optionEl, correctAnswer) {
    const options = document.querySelectorAll(".option");
    options.forEach(opt => opt.style.pointerEvents = "none"); 
    
    if (optionEl.textContent === correctAnswer) {
        optionEl.classList.add("correct");
        score++;
    } else {
        
        optionEl.classList.add("wrong");
        
        
        options.forEach(opt => {
            
            if (opt.textContent === correctAnswer) {
                opt.classList.add("correct");
            }
        });
    }
    
    nextBtn.classList.remove("hidden");
}

nextBtn.addEventListener("click", () => {
    currentQuestion++;
    if (currentQuestion < quizData.length) {
        loadQuestion();
    } else {
        showResult();
    }
});

function showResult() {
    const percentage = Math.round((score / quizData.length) * 100);
    document.getElementById("quizContainer").classList.add("hidden");
    resultDiv.innerHTML = `You scored ${percentage}% (${score}/${quizData.length})`;
    restartBtn.classList.remove("hidden");
}

restartBtn.addEventListener("click", () => {
    currentQuestion = 0;
    score = 0;
    resultDiv.innerHTML = "";
    restartBtn.classList.add("hidden");
    document.getElementById("quizContainer").classList.remove("hidden");
    loadQuestion();
});

loadQuestion();

